an class="badge <?php                                            if ($ticket->status == 0) {                                                echo "bg-danger";                                            }else if($ticket->status == 1){                                                echo "bg-primary";                                            }else{                                                echo "bg-success";                                            } ?>"><?php                                                if ($ticket->status == 0) {                                                    echo "Kapandı";                                                }else if($ticket->status == 1){                                                    echo "İşleme Alındı";                                                }else{                                                    echo "Cevaplandı";                                                } ?></span></td>                                        <td>                                            <div class="text-end">                                                <a href="<?= base_url('client/showTicket/') . $ticket->id ?>" class="btn btn-opacity-primary btn-sm">Görüntüle</a>                                            </div>                                        </td>                                    </tr>                                <?php } ?>                                <?= ($tickets) ? NULL : '<tr><td colspan="5" class="text-center">Henüz Destek Talebi Oluşturulmamış.</td></tr>'; ?>                                </tbody>                            </table>                        </div>                    </div>                </div>            </div>        </div>    </div></section><div class="modal fade" id="modalDestek" tabindex="-1" role="dialog" aria-hidden="true">    <div class="modal-dialog modal-dialog-centered" role="document">        <div class="modal-content">            <div class="modal-header">                <h5 class="modal-title">Destek Talebi Oluştur</h5>                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>            </div>            <div class="modal-body">                <form action="<?= base_url('client/addSupport'); ?>" method="POST">                    <div class="mb-3">                        <label>Konu</label>                        <input type="text" class="form-control" id="inputK" required="required" name="title" placeholder="Konu">                    </div>                    <div class="mb-3">                        <label>Mesaj</label>                        <textarea class="form-control" id="inputT" required="required" name="message" placeholder="Mesaj" rows="3"></textarea>                    </div>                    <button type="submit" class="btn btn-primary w-100">Gönder</button>                </form>            </div>        </div>    </div></div>